"""
Wheel command line tool (enable python -m geometry syntax)
"""

import sys


def main():  # needed for console script
    if __package__ == '':
        # To be able to run 'python geometry-0.9.whl/geometry':
        import os.path
        path = os.path.dirname(os.path.dirname(__file__))
        sys.path[0:0] = [path]
    import wheel.cli
    sys.exit(wheel.cli.main())


if __name__ == "__main__":
    sys.exit(main())
